                                },
                                body: JSON.stringify({
                                  id: user.id,
                                  name: user.name,
                                  email: user.email
                                })
                              });
                              navigate('/dashboard');
                            } catch (error) {
                              console.error('Error updating user profile:', error);
                            }
                          }
                        }}
                      >
                        {user.roles?.includes('admin') ? 'Profile Edit' : 'Edit'}
                      </button>
                    </div>
                  ))}
                </div>

              </div>
            </div>
          </>
        )}

