feedback_cycles.updated_code:
:root {
  font-family: system-ui, Avenir, Helvetica, Arial, sans-serif;
  line-height: 1.5;
  font-weight: 400;

  color-scheme: light dark;
  color: rgba(255, 255, 255, 0.95);
  background-color: #242424;

  font-synthesis: none;
  text-rendering: optimizeLegibility;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

*,
*::before,
*::after {
  box-sizing: border-box;
}

a {
  font-weight: 500;
  color: #646cff;
  text-decoration: inherit;
}
a:hover {
  color: #535bf2;
}

body {
  margin: 0;
  display: flex;
  place-items: center;
  min-width: 320px;
  min-height: 100vh;
  background-color: var(--background-color, #242424);
}

h1 {
  font-size: 3.2em;
  line-height: 1.1;
  margin-bottom: 1.2rem;
  color: inherit;
}

button {
  border-radius: 8px;
  border: 1px solid transparent;
  padding: 0.6em 1.2em;
  font-size: 1em;
  font-weight: 600;
  font-family: inherit;
  background-color: #1a1a1a;
  color: #fff;
  cursor: pointer;
  transition: border-color 0.25s, background 0.2s, color 0.2s;
  outline: none;
}
button:hover,
button:focus-visible {
  border-color: #646cff;
  background-color: #23238b;
  color: #fff;
}
button:focus {
  outline: 3px solid #646cff;
  outline-offset: 2px;
}

.form-group {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  margin-bottom: 1.2rem;
  width: 100%;
  max-width: 350px;
}

label {
  font-size: 1em;
  font-weight: 500;
  margin-bottom: 0.4em;
  color: inherit;
}

input[type="email"],
input[type="password"] {
  width: 100%;
  padding: 0.6em 0.8em;
  border: 1px solid #888;
  border-radius: 6px;
  font-size: 1em;
  background: #181818;
  color: #fff;
  transition: border-color 0.2s, box-shadow 0.2s;
  margin-bottom: 0.2em;
}
input[type="email"]:focus,
input[type="password"]:focus {
  border-color: #646cff;
  box-shadow: 0 0 0 2px #646cff33;
  outline: none;
  background: #222;
}

.error {
  color: #ff4d4f;
  font-size: 0.95em;
  margin-top: 0.1em;
  min-height: 1.2em;
  transition: color 0.2s;
}

@media (max-width: 600px) {
  body {
    padding: 1em;
  }
  h1 {
    font-size: 2em;
  }
  .form-group {
    max-width: 100%;
  }
}

@media (prefers-color-scheme: light) {
  :root {
    color: #213547;
    background-color: #ffffff;
  }
  a:hover {
    color: #747bff;
  }
  button {
    background-color: #f9f9f9;
    color: #213547;
  }
  button:hover,
  button:focus-visible {
    background-color: #e6e6ff;
    color: #213547;
  }
  input[type="email"],
  input[type="password"] {
    background: #fff;
    color: #213547;
    border: 1px solid #bdbdbd;
  }
  input[type="email"]:focus,
