  text-decoration: inherit;
}
a:hover {
  color: #535bf2;
}

body {
  margin: 0;
  min-width: 320px;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: var(--background-color, #242424);
}

h1 {
  font-size: 3.2em;
  line-height: 1.1;
}

button {
  border-radius: 8px;
  border: 1px solid transparent;
  padding: 0.6em 1.2em;
  font-size: 1em;
  font-weight: 500;
  font-family: inherit;
  background-color: #1a1a1a;
  cursor: pointer;
  transition: border-color 0.25s, box-shadow 0.2s;
}
button:hover {
  border-color: #646cff;
}
button:focus,
button:focus-visible {
  outline: 2px solid #646cff;
  outline-offset: 2px;
  box-shadow: 0 0 0 2px #646cff33;
}

form {
  background: rgba(36, 36, 36, 0.98);
  padding: 2.5em 2em 2em 2em;
  border-radius: 12px;
  box-shadow: 0 2px 16px 0 #00000022;
  min-width: 320px;
  max-width: 360px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  gap: 1.5em;
}

.form-group {
  display: flex;
  flex-direction: column;
  gap: 0.5em;
}

label {
  font-size: 1em;
  font-weight: 500;
  margin-bottom: 0.2em;
  color: inherit;
}

input[type="email"],
input[type="password"] {
  padding: 0.7em 1em;
  border: 1px solid #ccc;
  border-radius: 6px;
  font-size: 1em;
  font-family: inherit;
  background: #181818;
  color: #fff;
  transition: border-color 0.2s, box-shadow 0.2s;
}

input[type="email"]:focus,
input[type="password"]:focus {
  border-color: #646cff;
  outline: none;
  box-shadow: 0 0 0 2px #646cff33;
}

.error {
  color: #ff4d4f;
  font-size: 0.95em;
  margin-top: 0.2em;
  font-weight: 500;
  letter-spacing: 0.01em;
  min-height: 1.2em;
  display: block;
}

@media (prefers-color-scheme: light) {
  :root {
    color: #213547;
    background-color: #ffffff;
  }
  a:hover {
    color: #747bff;
  }
  button {
    background-color: #f9f9f9;
  }
  form {
    background: #fff;
    box-shadow: 0 2px 16px 0 #00000011;
  }
  input[type="email"],
  input[type="password"] {
    background: #fff;
    color: #213547;
    border: 1px solid #bbb;
  }
}

@media (max-width: 480px) {
  form {
    min-width: 0;
    width: 100%;
    padding: 1.2em 0.5em 1em 0.5em;
    box-sizing: border-box;
  }
  h1 {
    font-size: 2em;
  }
}

