  border-color: #ff4d4f;
  background: #2a1818;
}

.error {
  color: #ff4d4f;
  font-size: 0.95em;
  margin-top: 0.4em;
  font-weight: 500;
  letter-spacing: 0.01em;
}

form {
  background: rgba(36, 36, 36, 0.98);
  border-radius: 12px;
  padding: 2.5rem 2rem 2rem 2rem;
  box-shadow: 0 2px 16px 0 #00000022;
  display: flex;
  flex-direction: column;
  align-items: center;
  min-width: 320px;
  max-width: 400px;
  width: 100%;
  margin: 0 auto;
}

@media (max-width: 480px) {
  form {
    padding: 1.5rem 1rem 1rem 1rem;
    min-width: 0;
    max-width: 100vw;
  }
  .form-group {
    max-width: 100vw;
  }
  h1 {
    font-size: 2em;
  }
}

@media (prefers-color-scheme: light) {
  :root {
    color: #213547;
    background-color: #ffffff;
  }
  a:hover {
    color: #747bff;
  }
  button {
    background-color: #f9f9f9;
    color: #213547;
  }
  form {
    background: #fff;
    box-shadow: 0 2px 16px 0 #00000011;
  }
  input[type="email"],
  input[type="password"] {
    background: #fff;
    color: #213547;
    border: 1px solid #bbb;
  }
  input[type="email"]:focus,
  input[type="password"]:focus {
    border-color: #646cff;
    box-shadow: 0 0 0 2px #646cff22;
  }
  input[type="email"].error,
  input[type="password"].error {
    background: #fff0f0;
    border-color: #ff4d4f;
  }
}

