  height: 6em;
  padding: 1.5em;
  will-change: filter;
  transition: filter 300ms;
}
.logo:hover {
  filter: drop-shadow(0 0 2em #646cffaa);
}
.logo.react:hover {
  filter: drop-shadow(0 0 2em #61dafbaa);
}

@keyframes logo-spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

@media (prefers-reduced-motion: no-preference) {
  a:nth-of-type(2) .logo {
    animation: logo-spin infinite 20s linear;
  }
}

.card {
  padding: 2em;
}

.read-the-docs {
  color: #888;
}

client/src/App.css.updated_code:
#root {
  max-width: 1280px;
  margin: 0 auto;
  padding: 2rem;
  text-align: center;
}

/* Login Form Styles */
.login-form {
  max-width: 360px;
  margin: 2rem auto;
  padding: 2rem 2.5rem 1.5rem 2.5rem;
  background: #222;
  border-radius: 12px;
  box-shadow: 0 2px 16px 0 rgba(0,0,0,0.10);
  text-align: left;
}

@media (prefers-color-scheme: light) {
  .login-form {
    background: #fff;
    box-shadow: 0 2px 16px 0 rgba(0,0,0,0.06);
  }
}

.form-group {
  margin-bottom: 1.5rem;
  display: flex;
  flex-direction: column;
}

label {
  font-size: 1rem;
  font-weight: 500;
  margin-bottom: 0.5rem;
  color: #fff;
}

@media (prefers-color-scheme: light) {
  label {
    color: #213547;
  }
}

input[type="email"],
input[type="password"] {
  padding: 0.7em 1em;
  border: 1px solid #444;
  border-radius: 6px;
  font-size: 1em;
  background: #181818;
  color: #fff;
  transition: border-color 0.2s, box-shadow 0.2s;
  outline: none;
}

input[type="email"]:focus,
input[type="password"]:focus {
  border-color: #646cff;
  box-shadow: 0 0 0 2px #646cff33;
}

@media (prefers-color-scheme: light) {
  input[type="email"],
  input[type="password"] {
    background: #f9f9f9;
    color: #213547;
    border: 1px solid #ccc;
  }
}

.error {
  color: #ff4d4f;
  font-size: 0.95em;
  margin-top: 0.4em;
  font-weight: 500;
  letter-spacing: 0.01em;
}

button[type="submit"] {
  width: 100%;
  margin-top: 0.5rem;
  padding: 0.7em 1.2em;
  border-radius: 8px;
  border: 1px solid transparent;
  font-size: 1.1em;
  font-weight: 600;
  font-family: inherit;
  background-color: #646cff;
  color: #fff;
  cursor: pointer;
  transition: background 0.2s, border-color 0.2s;
  box-shadow: 0 1px 4px 0 rgba(0,0,0,0.07);
}

button[type="submit"]:hover,
button[type="submit"]:focus {
  background-color: #535bf2;
  border-color: #535bf2;
}

button[type="submit"]:focus-visible {
  outline: 3px auto -webkit-focus-ring-color;
}

/* Existing styles */
.logo {
  height: 6em;
  padding: 1.5em;
  will-change: filter;
  transition: filter 300ms;
}
.logo:hover {
  filter: drop-shadow(0 0 2em #646cffaa);
}
.logo.react:hover {
  filter: drop-shadow(0 0 2em #61dafbaa);
}

@keyframes logo-spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

@media (prefers-reduced-motion: no-preference) {
  a:nth-of-type(2) .logo {
    animation: logo-spin infinite 20s linear;
  }
}

.card {
  padding: 2em;
}

.read-the-docs {
  color: #888;
}
